        else
            inflow_direct = grid_vicout(:,5);
            inflow_base = grid_vicout(:,6);
        end
        
        flowlen = routpar_outlet(n,4);
        route_par = [routpar_outlet(n,5),routpar_outlet(n,6),routpar_outlet(n,7),routpar_outlet(n,8)];
        if n == outlet_ind
            isOutlet = 1;
        else
