        set(handles.edit_VICglobalconfigfile_batch,'string',[])
        
        set(handles.listbox_globalconfiglist, 'min', 0, 'max', 2);
        set(handles.listbox_globalconfiglist,'Value',[]);
        
    elseif strcmp(ext,'.xls') || strcmp(ext,'.xlsx') || strcmp(ext,'.csv')
